import React, { useEffect, useState } from "react";
import axios from "axios";
import keys from "../../config/keys";
import { Card, CardTitle, CardBody } from "@progress/kendo-react-layout";
import "../pages.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAddressCard } from "@fortawesome/free-solid-svg-icons";

const Skill = () => {
  const [data, setData] = useState({});

  useEffect(() => {
    const url = keys.backendURI + "/api/education";
    const fetchData = async () => {
      const result = await axios(url);
      setData(result.data);
      // console.log(data.data[1]);
    };
    fetchData();
  }, []);
  if (!data.data) return null;
  // // empty render until we get data

  // if we get data we do have education details
  return (
    <div className="component-section">
      <div className="content-heading">SKILLS</div>
      <div className="card-content-row">
        <div className="card-content">
          <Card
            style={{
              margin: 100,
              marginLeft: 100,
              marginTop: 100,
              borderWidth: 2,
            }}
          >
            <CardBody className="card-body">
              <CardTitle>
                <h2>Knowledge</h2>
              </CardTitle>
              <br />
              <div className="skills">
                <div className="column-align">
                  <div className="Cpp-image">
                    <h3>
                      <FontAwesomeIcon icon={faAddressCard} size="8x" />
                    </h3>
                  </div>
                  <div>
                    <h3>{data.data[0].name}</h3>
                  </div>
                </div>
                <div className="column-align">
                  <div className="Cpp-image">
                    <h3>
                      <FontAwesomeIcon icon={faAddressCard} size="8x" />
                    </h3>
                  </div>
                  <div>
                    <h3>C++</h3>
                  </div>
                </div>
                <div className="column-align">
                  <div className="Cpp-image">
                    <h3>
                      <FontAwesomeIcon icon={faAddressCard} size="8x" />
                    </h3>
                  </div>
                  <div>
                    <h3>C++</h3>
                  </div>
                </div>
              </div>
            </CardBody>
          </Card>
        </div>
      </div>
      <div className="card-content">
        <Card
          style={{
            margin: 100,
            marginLeft: 100,
            marginTop: 100,
            borderWidth: 2,
          }}
        >
          <CardBody className="card-body">
            <CardTitle>
              <h2>Industry Knowledge</h2>
            </CardTitle>
            <br />
            <div className="skills"></div>
          </CardBody>
        </Card>
      </div>
      <div className="card-content">
        <Card
          style={{
            margin: 100,
            marginLeft: 100,
            marginTop: 100,
            borderWidth: 2,
          }}
        >
          <CardBody className="card-body">
            <CardTitle>
              <h2>Tools And Technologies</h2>
            </CardTitle>
            <br />
            <div className="skills"></div>
          </CardBody>
        </Card>
      </div>
    </div>
  );
};

export default Skill;
