import React from "react";
import axios from "axios";
import { Card, CardTitle, CardBody } from "@progress/kendo-react-layout";
import "../pages.css";

const About = (props) => {
  let desc;
  const getInfo = () => {
    axios.get("https://b14d3ee816fd.ngrok.io/api/about").then((response) => {
      const data = response.data;
      desc = data.data[0].description;
      console.log(desc);
      // this.setState({ desc: data });
      console.log("Info recieved !!");
    });
  };

  return (
    <div className="component-section">
      <div className="content-heading">ABOUT</div>
      <div className="card-content">
        <Card
          style={{
            margin: 100,
            marginLeft: 100,
            marginTop: 100,
            borderWidth: 2,
          }}
        >
          <CardBody className="card-body">
            <CardTitle>
              {/* {getInfo()} */}
              <h1>{props.desc}</h1>
            </CardTitle>

            <br />
            <p className="discription">
              {/* Fetch the ABOUT through API */}
              Some quick example text to build on the card title and make up the
              bulk of the card content.
            </p>
          </CardBody>
        </Card>
      </div>
    </div>
  );
};

export default About;

